import React from "react";
import ReactDOM from "react-dom";
import pic from "./bluelogo.png";
import "./index.css";

// .heading {
// 	color: #fa9191;
// 	text-align: center;
// 	text-transform: capitalize;
// 	font-weight: bold;
// 	text-shadow: 0px 2px 4px #ffe9c5;
// 	margin: 50px 0;
// 	font-family: "Montserrat", sans-serif;
// }

const fname = "Manu";
const lname = "Gupta";
const currT = new Date(2021, 2, 6, 14).getHours();
let greet = "";
const cssStyle = {};
if (currT >= 1 && currT < 12) {
	greet = "Good Morning!!";
	cssStyle.color = "Pink";
} else if (currT >= 12 && currT < 19) {
	greet = "Good Afternoon!!";
	cssStyle.color = "Orange";
} else {
	greet = "Good Night!!";
	cssStyle.color = "Blue";
}
const links = "https://www.linkedin.com/in/manugupta9780/";

const heading = {
	color: "#fa9191",
	textAlign: "center",
	textTransform: "capitalize",
	fontWeight: "bold",
	textShadow: "0px 2px 4px #ffe9c5",
	margin: "50px 0",
	fontFamily: '"Montserrat", sans-serif',
};

ReactDOM.render(
	<>
		<h1 style={heading}>{`My name is ${fname} ${lname}`}</h1>
		<div className="img_div">
			<a href={links} target="_manu">
				<img src={pic} alt="randomImage" />
			</a>
		</div>
		<div>
			<h1
				style={{
					color: "green",
					textAlign: "center",
					fontWeight: "bold",
					fontFamily: '"Montserrat", sans-serif',
				}}>
				Hello Sir,<span style={cssStyle}>{greet}</span>
			</h1>
		</div>
	</>,
	document.getElementById("root")
);
