import React from "react";
import ReactDOM from "react-dom";

const fname = "Manu";
const lname = "Gupta";
const curr = new Date();

ReactDOM.render(
	<>
		<h1>{`My name is ${fname} ${lname}`}</h1>
		<p>{`Current Date is ${curr.toLocaleDateString()}`}</p>
		<p>{`Current Time is ${curr.toLocaleTimeString()}`}</p>
	</>,
	document.getElementById("root")
);
