import React from "react";
import "./index.css";

const currT = new Date(2021, 2, 6, 23).getHours();
let greet = "";
const cssStyle = {};
if (currT >= 1 && currT < 12) {
	greet = "Good Morning!!";
	cssStyle.color = "Pink";
} else if (currT >= 12 && currT < 19) {
	greet = "Good Afternoon!!";
	cssStyle.color = "Orange";
} else {
	greet = "Good Night!!";
	cssStyle.color = "Blue";
}
function Heading() {
	return (
		<div className="Greet">
			<h1>
				Hello Sir,<span style={cssStyle}>{greet}</span>
			</h1>
		</div>
	);
}

export default Heading;
